DECLARE @PKTableName VARCHAR(100) = 'COM_Department'
 
DECLARE @PKName varchar(100) = (SELECT name FROM sys.indexes WHERE OBJECT_NAME(object_id) = @PKTableName AND is_primary_key = 1),
        @FKName varchar(100),
        @PKColumnName varchar(30) = (SELECT name FROM sys.columns WHERE OBJECT_NAME(object_id) = @PKTableName AND is_identity = 1),
        @FKColumnName varchar(100),
        @IndexName varchar(100) = (SELECT TOP 1 name FROM sys.indexes WHERE object_id = OBJECT_ID(@PKTableName) and type_desc = 'CLUSTERED'),
        @table varchar(100),
        @sql varchar(max) = '',
        @NewLineChar AS char(2) = char(13) + char(10)
         
IF (@PKName != ISNULL(@IndexName, ''))
BEGIN
 
    IF OBJECT_ID('tempdb..#FKAgainstTableList') IS NOT NULL
    BEGIN
        DROP TABLE #FKAgainstTableList
    END
     
    -- Create temp table and get FK constraints
    CREATE TABLE #FKAgainstTableList(
        TableName varchar(100) not null,
        FKColumnName varchar(200) not null,
        FKName varchar(100) not null
    )
 
    INSERT INTO #FKAgainstTableList
        SELECT OBJECT_NAME(sys.foreign_key_columns.parent_object_id) [Table],sys.columns.name [FKColumnName],sys.foreign_keys.name [FKName]
            FROM sys.foreign_keys INNER JOIN sys.foreign_key_columns
            ON sys.foreign_keys.object_id = sys.foreign_key_columns.constraint_object_id
            INNER JOIN sys.columns ON sys.columns.object_id = sys.foreign_keys.parent_object_id AND sys.columns.column_id = sys.foreign_key_columns.parent_column_id
            WHERE OBJECT_NAME(sys.foreign_keys.referenced_object_id) = @PKTableName
 
    -------------------------------Disable constraint on FK tables
    DECLARE table_cur1 CURSOR FOR
        SELECT * FROM #FKAgainstTableList
 
    OPEN table_cur1
    FETCH NEXT FROM table_cur1 INTO @table, @FKColumnName, @FKName
    WHILE  @@FETCH_STATUS = 0
        BEGIN
            SET @sql += 'ALTER TABLE ['+ @table +'] DROP CONSTRAINT ['+ @FKName +']' + @NewLineChar
             
            FETCH NEXT FROM table_cur1 INTO @table, @FKColumnName, @FKName
        END
 
    CLOSE table_cur1
    DEALLOCATE table_cur1
 
    --------------------------------Drop and recreate CLUSTERED index on PK
    IF EXISTS (SELECT 1 FROM sys.indexes WHERE object_id = OBJECT_ID(@PKTableName) AND name = @IndexName)
    BEGIN
        SET @sql += 'DROP INDEX ['+ @IndexName +'] ON ['+ @PKTableName +']' + @NewLineChar
    END
 
    IF EXISTS (SELECT 1 FROM sys.indexes WHERE object_id = OBJECT_ID(@PKTableName) AND name = @PKName)
    BEGIN
        SET @sql += 'ALTER TABLE ['+ @PKTableName +'] DROP CONSTRAINT ['+ @PKName +']' + @NewLineChar
    END
    ELSE
    BEGIN
        SET @sql += 'DROP INDEX ['+ @PKName +'] ON ['+ @PKTableName +']' + @NewLineChar
    END
 
    SET @sql += 'ALTER TABLE ['+ @PKTableName +'] ADD CONSTRAINT ['+ @PKName +'] PRIMARY KEY CLUSTERED (['+ @PKColumnName +'])' + @NewLineChar
 
    --------------------------------Recreate FK constraints on FK tables
    DECLARE table_cur2 CURSOR FOR
        SELECT * FROM #FKAgainstTableList
 
    OPEN table_cur2
    FETCH NEXT FROM table_cur2 INTO @table, @FKColumnName, @FKName
    WHILE @@FETCH_STATUS = 0
        BEGIN
            SET @sql += 'ALTER TABLE ['+ @table +'] WITH CHECK ADD CONSTRAINT ['+ @FKName +'] FOREIGN KEY(['+ @FKColumnName +']) REFERENCES ['+ @PKTableName +'] (['+ @PKColumnName +'])' + @NewLineChar
            SET @sql += 'ALTER TABLE ['+ @table +'] CHECK CONSTRAINT ['+ @FKName +']'
 
            FETCH NEXT FROM table_cur2 INTO @table, @FKColumnName, @FKName
        END
 
    CLOSE table_cur2
    DEALLOCATE table_cur2
     
    DROP TABLE #FKAgainstTableList
 
    -- Run script
    EXEC(@sql)
END


