DECLARE @parentID int;
DECLARE issuesWithVariants CURSOR FOR   
	SELECT DISTINCT IssueVariantOfIssueID 
	FROM Newsletter_NewsletterIssue
	WHERE IssueVariantOfIssueID IS NOT NULL

OPEN issuesWithVariants  
FETCH NEXT FROM issuesWithVariants   
INTO @parentID

WHILE @@FETCH_STATUS = 0  
BEGIN
	IF EXISTS (SELECT IssueID FROM Newsletter_NewsletterIssue WHERE IssueVariantOfIssueID = @parentID AND IssueVariantName IS NULL) 
	BEGIN
		FETCH NEXT FROM issuesWithVariants   
		INTO @parentID
		CONTINUE
	END

	ELSE IF EXISTS (SELECT IssueID FROM Newsletter_NewsletterIssue WHERE IssueVariantOfIssueID = @parentID AND IssueVariantName = 'Original') 
	BEGIN
		DECLARE @firstVariantNamedOriginal int
		SET @firstVariantNamedOriginal = (SELECT TOP 1 IssueID FROM  Newsletter_NewsletterIssue 
		WHERE IssueVariantOfIssueID = @parentID AND IssueVariantName = 'Original')
		
		UPDATE Newsletter_NewsletterIssue
		SET IssueVariantName = NULL
		WHERE IssueID = @firstVariantNamedOriginal
	END
	ELSE
	BEGIN
		DECLARE @firstVariant int
		SET @firstVariant = (SELECT TOP 1 IssueID FROM  Newsletter_NewsletterIssue 
		WHERE IssueVariantOfIssueID = @parentID)
	
		UPDATE Newsletter_NewsletterIssue 
		SET IssueVariantName = NULL
		WHERE IssueID = @firstVariant
	END

	FETCH NEXT FROM issuesWithVariants   
	INTO @parentID
END
CLOSE issuesWithVariants 
DEALLOCATE issuesWithVariants
GO

