DECLARE @subscriberID INT
DECLARE @newsletterID INT
DECLARE @newsletterSiteID INT
DECLARE @subscriberNewsletterID INT
DECLARE @contactID INT

DECLARE inconsistent_data CURSOR FOR   
SELECT subscriber.SubscriberID, newsletter.NewsletterID, subscription.SubscriberNewsletterID, newsletter.NewsletterSiteID, subscriber.SubscriberRelatedID FROM Newsletter_Subscriber subscriber
	INNER JOIN Newsletter_SubscriberNewsletter subscription ON subscriber.SubscriberID = subscription.SubscriberID
	INNER JOIN Newsletter_Newsletter newsletter ON newsletter.NewsletterID = subscription.NewsletterID
WHERE newsletter.NewsletterSiteID != subscriber.SubscriberSiteID

OPEN inconsistent_data

FETCH NEXT FROM inconsistent_data
INTO @subscriberID, @newsletterID, @subscriberNewsletterID, @newsletterSiteID, @contactID

WHILE @@FETCH_STATUS = 0  
BEGIN  
	-- Check if exists duplicate  subscription
	IF NOT EXISTS(
		SELECT 1 FROM Newsletter_Subscriber subscriber INNER JOIN Newsletter_SubscriberNewsletter subscription ON subscriber.SubscriberID = subscription.SubscriberID
		WHERE subscriber.SubscriberID != @subscriberID 
			AND subscription.NewsletterID = @newsletterID
			AND subscriber.SubscriberRelatedID = @contactID) 
	BEGIN
		-- Exists only inconsistent cross-site subscription, so inconsistent subscription should be fixed
		DECLARE @newSubscriberID INT

		-- Create missing subscriber (it is copy of merged subscriber but proper siteID is used)
		INSERT INTO Newsletter_Subscriber (SubscriberEmail, SubscriberFirstName, SubscriberLastName, SubscriberSiteID, SubscriberGUID, SubscriberCustomData, SubscriberType, SubscriberRelatedID, SubscriberLastModified, SubscriberFullName, SubscriberBounces) 
		SELECT  SubscriberEmail, SubscriberFirstName, SubscriberLastName, @newsletterSiteID, SubscriberGUID, SubscriberCustomData, SubscriberType, SubscriberRelatedID, SubscriberLastModified, SubscriberFullName, SubscriberBounces 
			FROM Newsletter_Subscriber WHERE SubscriberID = @subscriberID

		SET @newSubscriberID = (SELECT SCOPE_IDENTITY())
   
		-- Direct incorrect cross-site subscription to correct subscriber
		UPDATE Newsletter_SubscriberNewsletter
		SET SubscriberID = @newSubscriberID
		WHERE SubscriberNewsletterID = @subscriberNewsletterID
	END
	ELSE
	BEGIN
		-- Exist inconsistent cross-site subscription and duplicate  correct subscription, so inconsistent one can be removed
		DELETE Newsletter_SubscriberNewsletter
		WHERE SubscriberNewsletterID = @subscriberNewsletterID
	END

	FETCH NEXT FROM inconsistent_data
	INTO @subscriberID, @newsletterID, @subscriberNewsletterID, @newsletterSiteID, @contactID
END   
CLOSE inconsistent_data;  
DEALLOCATE inconsistent_data; 
