	DECLARE class_cursor CURSOR FOR   
	SELECT [ClassFormDefinition] FROM [CMS_Class] WHERE ClassFormDefinition LIKE N'%Maxlenght%' OR ClassFormDefinition LIKE N'%Minlenght%'
	
	OPEN class_cursor  
	
