				SET @classFormDefinition.modify('delete (form/field/settings[controlname/text() = "HTML5Input"]/Maxlenght)[1]');
			END

		WHILE (@classFormDefinition.exist('(form/field/settings[controlname/text() = "HTML5Input"]/Minlenght)[1]') = 1)
			BEGIN
				SET @value =  @classFormDefinition.value('(form/field/settings[controlname/text() = "HTML5Input"]/Minlenght)[1]', 'nvarchar(max)'); 
				
				SET @classFormDefinition.modify('insert <Minlength>{sql:variable("@value")}</Minlength> after (form/field/settings[controlname/text() = "HTML5Input"]/Minlenght)[1]');
				
