		FETCH NEXT FROM class_cursor INTO @classFormDefinition   
	END
	CLOSE class_cursor;  
	DEALLOCATE class_cursor; 

	-- Fix usages of the HTML5Input control in alternative forms
	DECLARE @alternativeFormDefinition xml;

	-- Minimize the number of rows
